$(document).ready( function () {
	$("body").css("background-color" , "#f9f9f9");
	$("table, th, td").css("border" , "1px solid black");
	$("th, td").css("padding" , "4px");
	$("table").css({"width" : "85%" , "margin" : "10px auto"});
	$("input[type=submit], input[type=reset").css({"padding-top" : "2px" , "padding-bottom" : "4px" , "padding-right" : "6px" , "padding-left" : "6px"});
});
